import { useState, useEffect } from 'react';
function Palabras(props)
{
    const [contador, setContador] = useState(1);

    useEffect(() => {
        let cont = 1;
        console.log(props.texto.length);
        for(let i=0; i<props.texto.lenght;i++)
        {
            if(props.texto[i] === ' ')
            {
                alert("si");
                cont++;
            }
        }
        setContador(cont)
        
    }, [props.texto])
    return (
    <div>
        <p><input type="text" onChange={props.cambiar} /></p>
        <p>{props.numero}/{props.maximo}</p>
        <p>{contador}</p>
    </div>);
}
export default Palabras
