import { useState, useEffect } from 'react';
import { InputText } from 'primereact/inputtext';
import { Card } from 'primereact/card';

function Palabras(props) {
  const [contador, setContador] = useState(1);

  useEffect(() => {
    let cont = 1;
    for(let i=0; i<props.texto.length;i++) {
      if(props.texto[i] === ' ') {
        if(props.texto[i+1] !== ' '  && i+1 !== props.texto.length) {
          cont++;
        }
      }
    }
    setContador(cont)
  }, [props.texto]);

  return (
    <Card>
      <div>
        <span>
          <i></i>
        </span>
        <InputText
          type="text"
          onChange={props.cambiar}
          maxLength={props.maximo}
          value={props.texto}
        />
        <span>{props.numero}/{props.maximo}</span>
      </div>
      <div>
        <p>Cantidad de palabras:</p>
        <h2>{contador}</h2>
      </div>
    </Card>
  );
}

export default Palabras;
