function Palabras(props)
{
    function cantidadPalabras()
    {
        var contador=1;
        for(let i=0; i<props.texto.lenght;i++)
        {
            if(props.texto[i]==" ")
            {
                contador++;
            }
        }
        return contador;
    }
    return (
    <div>
        <p><input type="text" onChange={props.cambiar} /></p>
        <p>{props.numero}/{props.maximo}</p>
        <p>{cantidadPalabras}</p>
    </div>);
}
export default Palabras
